<?php if (!defined('THINK_PATH')) exit(); echo W("Main",array('module'=>MODULE_NAME,'action'=>ACTION_NAME,'do'=>$_GET['do']));?><div class="layout-main"><div id="breadclumb" class="box"><h3><strong><?php echo lang('breadclumb_colon');?></strong><?php echo lang(MODULE_NAME);?><span></span><?php echo lang('category');?></h3></div><div id="CooperationMain" class="box clear-fix"><div class="layout-block-header"><a class="btn btn-ok" href="<?php echo U('Item/category',array('do'=>'edit'));?>"><?php echo lang('add');?></a></div><div class="ui-table"><div class="ui-table-body ui-table-body-hover"><table cellpadding="0" cellspacing="0" width="100%" ><thead><tr class="ui-table-head"><th class="ui-table-hcell ui-table-hcell-sort" width="40"><?php echo lang('id');?></th><th class="ui-table-hcell ui-table-hcell-sort"><?php echo lang('name');?></th><th class="ui-table-hcell ui-table-hcell-sort"><?php echo lang('alias');?></th><th class="ui-table-hcell ui-table-hcell-sort"><?php echo lang('count');?></th><th class="ui-table-hcell ui-table-hcell-sort"><?php echo lang('action');?></th></tr></thead><tbody><?php if(is_array($list)): $i = 0; $__LIST__ = $list;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><tr id="row-<?php echo ($vo["tags_type_id"]); ?>"><td><?php echo ($vo["tags_type_id"]); ?></td><td><span style="margin-left:<?php echo ($vo['level']-1)*2; ?>em;"><?php if(($vo["level"]) > "1"): ?>└─<?php endif; if(!empty($vo["icon"])): ?><i class="icon-<?php echo ($vo["icon"]); ?>"></i><?php endif; echo ($vo["name"]); ?></span><?php if(!empty($vo["image"])): ?><a href="__PUBLIC__/Uploads<?php echo ($vo["image"]); ?>" title="<?php echo lang('image');?>" target="_blank"><img src="__PUBLIC__/Assets/img/pic.jpg" /></a><?php endif; ?></td><td><?php echo ($vo["alias"]); ?></td><td><?php echo ($vo["count"]); ?></td><td class="action"><a href="<?php echo U('Item/category',array('do'=>'edit','id'=>$vo['tags_type_id']));?>"><?php echo lang('edit');?></a> |
